// src/pages/SwipeProject.js
import React, { useState } from 'react';
import { motion, useMotionValue, useTransform } from 'framer-motion';
import '../styles/SwipeProject.css';

function SwipeProject() {
  // Hardcoded project data
  const projects = [
    { id: 1, title: 'Project A', description: 'Description of Project A' },
    { id: 2, title: 'Project B', description: 'Description of Project B' },
    { id: 3, title: 'Project C', description: 'Description of Project C' },
  ];

  // State to manage the current card index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Motion value and transformation for swipe effect
  const x = useMotionValue(0);
  const rotate = useTransform(x, [-200, 200], [-15, 15]);

  // Handler for swipe action
  const handleDragEnd = (event, info) => {
    if (info.offset.x < -150) {
      // Left swipe: show next card
      setCurrentIndex((prev) => (prev + 1) % projects.length);
    } else if (info.offset.x > 150) {
      // Right swipe: show next card
      setCurrentIndex((prev) => (prev + 1) % projects.length);
    }
    // Reset x position after swipe
    x.set(0);
  };

  return (
    <div className="swipe-project">
      <h1>Swipe to Match with Projects</h1>
      <h3>Swipe left to reject, right for more details</h3>
      {projects[currentIndex] && (
        <motion.div
          className="project-card"
          style={{ x, rotate }}
          drag="x"
          onDragEnd={handleDragEnd}
          dragConstraints={{ left: 0, right: 0 }}
          initial={{ scale: 0.9 }}
          animate={{ scale: 1 }}
          whileTap={{ scale: 0.95 }}
        >
          <h2>{projects[currentIndex].title}</h2>
          <p>{projects[currentIndex].description}</p>
        </motion.div>
      )}
    </div>
  );
}

export default SwipeProject;
